<?php		class Conexao {		public function Connect(){			try {				$this->user = "portal123";				$this->pass = "portal@123";				$this->host = "portal123.mysql.dbaas.com.br";				$this->base = "portal123";				$parametros = array(PDO::MYSQL_ATTR_INIT_COMMAND => "SET NAMES UTF8"); 				$this->file = "mysql:host=" . $this->host . ";dbname=" . $this->base;				$this->pdo = new PDO($this->file, $this->user, $this->pass, $parametros);				$this->pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);				$this->pdo->setAttribute(PDO::ATTR_EMULATE_PREPARES, false);				$this->pdo->setAttribute(PDO::ATTR_PERSISTENT, true);				if (!$this->pdo) {					echo "Erro na conexão";				}				return $this->pdo;			} catch(PDOException $ex){			echo "Erro no sistema" . $ex->getMessage();			}		}	}?>